import React, { useState, useRef, useEffect } from "react";
import { DragDropContext, Droppable, Draggable } from "@hello-pangea/dnd";

const initialFields = [
  { id: "field-1", label: "Text Input", type: "text", placeholder: "Enter text", required: false },
  { id: "field-2", label: "Textarea", type: "textarea", placeholder: "Enter your message", required: false },
  { 
    id: "field-3", 
    label: "Select Dropdown", 
    type: "select", 
    options: ["Option 1", "Option 2", "Option 3"], 
    required: false 
  },
  { id: "field-4", label: "Checkbox", type: "checkbox", required: false },
  { 
    id: "field-5", 
    label: "Radio Button", 
    type: "radio", 
    options: ["Option A", "Option B"], 
    required: false 
  },
  { id: "field-6", label: "Date Picker", type: "date", required: false },
  { id: "field-7", label: "File Upload", type: "file", required: false },
];

const FormBuilder = () => {
  const [fields, setFields] = useState(initialFields);
  const [containerFields, setContainerFields] = useState([]);
  const [selectedField, setSelectedField] = useState(null);

  const onDragEnd = (result) => {
    const { source, destination } = result;
    if (!destination) return;

    if (source.droppableId === "fields" && destination.droppableId === "container") {
      const draggedField = fields[source.index];
      const newField = {
        ...draggedField,
        id: `${draggedField.id}-${Date.now()}`, // Generate a unique ID for the field
      };
      setContainerFields([...containerFields, newField]);
    } else if (source.droppableId === "container" && destination.droppableId === "container") {
      const reorderedFields = Array.from(containerFields);
      const [moved] = reorderedFields.splice(source.index, 1);
      reorderedFields.splice(destination.index, 0, moved);
      setContainerFields(reorderedFields);
    } else if (source.droppableId === "container" && destination.droppableId === "fields") {
      // Remove field from containerFields when dragged back to available fields
      const updatedContainerFields = Array.from(containerFields);
      updatedContainerFields.splice(source.index, 1);
      setContainerFields(updatedContainerFields);

      // If the removed field is currently selected, deselect it
      const removedField = containerFields[source.index];
      if (selectedField && selectedField.id === removedField.id) {
        setSelectedField(null);
      }
    }
  };

  const handleFieldUpdate = (id, key, value) => {
    const updateFields = (fieldsList) =>
      fieldsList.map((field) =>
        field.id === id ? { ...field, [key]: key === "required" ? !field.required : value } : field
      );

    setFields(updateFields(fields));
    setContainerFields(updateFields(containerFields));
    setSelectedField((prev) => (prev?.id === id ? { ...prev, [key]: value } : prev));
  };

  const FieldEditor = ({ field }) => {
    const [localField, setLocalField] = useState(field);
    const inputRef = useRef(null);

    // Maintain focus when the component re-renders
    useEffect(() => {
      if (inputRef.current) {
        inputRef.current.focus();
      }
    }, [localField]);

    const handleLocalChange = (key, value) => {
      setLocalField((prev) => ({ ...prev, [key]: value }));
      handleFieldUpdate(field.id, key, value);
    };

    return (
      <div style={{ border: "1px solid #ddd", padding: "10px", marginTop: "10px" }}>
        <h4>Edit Field</h4>
        <label>
          Label:
          <input
            type="text"
            ref={inputRef}
            value={localField.label}
            onChange={(e) => handleLocalChange("label", e.target.value)}
          />
        </label>
        <br />
        {localField.type === "text" && (
          <>
            <label>
              Placeholder:
              <input
                type="text"
                value={localField.placeholder}
                onChange={(e) => handleLocalChange("placeholder", e.target.value)}
              />
            </label>
            <br />
          </>
        )}
        {localField.type === "textarea" && (
          <>
            <label>
              Placeholder:
              <input
                type="text"
                value={localField.placeholder}
                onChange={(e) => handleLocalChange("placeholder", e.target.value)}
              />
            </label>
            <br />
          </>
        )}
        {localField.type === "select" && (
          <>
            <label>
              Options (comma separated):
              <input
                type="text"
                value={localField.options.join(", ")}
                onChange={(e) =>
                  handleLocalChange("options", e.target.value.split(",").map((opt) => opt.trim()))
                }
              />
            </label>
            <br />
          </>
        )}
        {localField.type === "radio" && (
          <>
            <label>
              Options (comma separated):
              <input
                type="text"
                value={localField.options.join(", ")}
                onChange={(e) =>
                  handleLocalChange("options", e.target.value.split(",").map((opt) => opt.trim()))
                }
              />
            </label>
            <br />
          </>
        )}
        <label>
          Required:
          <input
            type="checkbox"
            checked={localField.required}
            onChange={() => handleLocalChange("required", !localField.required)}
          />
        </label>
      </div>
    );
  };

  const renderField = (field) => {
    switch (field.type) {
      case "text":
        return (
          <input
            type="text"
            placeholder={field.placeholder}
            required={field.required}
            style={{ minWidth: "200px", marginBottom: "10px" }}
          />
        );
      case "textarea":
        return (
          <textarea
            placeholder={field.placeholder}
            required={field.required}
            style={{ minWidth: "200px", minHeight: "100px", marginBottom: "10px" }}
          />
        );
      case "select":
        return (
          <select required={field.required}>
            {field.options.map((option, idx) => (
              <option key={idx} value={option}>
                {option}
              </option>
            ))}
          </select>
        );
      case "checkbox":
        return <input type="checkbox" required={field.required} />;
      case "radio":
        return field.options.map((option, idx) => (
          <label key={idx}>
            <input type="radio" name={field.id} required={field.required} />
            {option}
          </label>
        ));
      case "date":
        return <input type="date" required={field.required} />;
      case "file":
        return <input type="file" required={field.required} />;
      default:
        return null;
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>Form Builder</h1>
      <DragDropContext onDragEnd={onDragEnd}>
        <div style={{ display: "flex", gap: "20px" }}>
          <Droppable droppableId="fields">
            {(provided) => (
              <div
                ref={provided.innerRef}
                {...provided.droppableProps}
                style={{ width: "300px", border: "1px solid #ddd", padding: "10px" }}
              >
                <h3>Available Fields</h3>
                {fields.map((field, index) => (
                  <Draggable key={field.id} draggableId={field.id} index={index}>
                    {(provided) => (
                      <div
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        style={{
                          ...provided.draggableProps.style,
                          margin: "5px 0",
                          padding: "10px",
                          backgroundColor: "#f0f0f0",
                          border: "1px solid #ccc",
                          borderRadius: "5px",
                        }}
                        onClick={() => setSelectedField(field)}
                      >
                        {field.label}
                      </div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>

          <Droppable droppableId="container">
            {(provided) => (
              <div
                ref={provided.innerRef}
                {...provided.droppableProps}
                style={{
                  flex: 1,
                  border: "2px dashed #bbb",
                  padding: "10px",
                  minHeight: "300px",
                  backgroundColor: "#fafafa",
                }}
              >
                <h3>Form Preview</h3>
                {containerFields.map((field, index) => (
                  <Draggable key={field.id} draggableId={field.id} index={index}>
                    {(provided) => (
                      <div
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        style={{
                          ...provided.draggableProps.style,
                          margin: "5px 0",
                          padding: "10px",
                          backgroundColor: "#e0f7fa",
                          border: "1px solid #00acc1",
                          borderRadius: "5px",
                        }}
                        onClick={() => setSelectedField(field)}
                      >
                        <label>{field.label}</label>
                        {renderField(field)}
                      </div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>
        </div>
      </DragDropContext>

      {selectedField && <FieldEditor field={selectedField} />}
    </div>
  );
};

export default FormBuilder;
