import React, { useState } from 'react';
import { DragDropContext, Droppable, Draggable } from '@hello-pangea/dnd';

const initialFields = [
  { id: 'field-1', label: 'First Name' },
  { id: 'field-2', label: 'Email' },
  { id: 'field-3', label: 'Date of Birth' },
];

const FormBuilder = () => {
  const [fields, setFields] = useState(initialFields);
  const [containerFields, setContainerFields] = useState([]);

  // Handle drag and drop logic
  const onDragEnd = (result) => {
    const { destination, source, draggableId } = result;

    // If dropped outside the list
    if (!destination) {
      return;
    }

    // Case 1: Field dropped into a container
    if (destination.droppableId === 'droppable-container') {
      const field = fields.find(f => f.id === draggableId);
      if (!field) return;

      // Add the field to the container if it's not already there
      if (!containerFields.some(f => f.id === field.id)) {
        setContainerFields([...containerFields, field]);
      }
      return;
    }

    // Case 2: Field dragged out of the container
    if (source.droppableId === 'droppable-container' && destination.droppableId === 'droppable-fields') {
      const field = containerFields.find(f => f.id === draggableId);
      if (!field) return;

      // Remove the field from the container
      setContainerFields(containerFields.filter(f => f.id !== draggableId));

      // Add the field back to the main fields list
      setFields([...fields, field]);
      return;
    }

    // Case 3: Reorder the fields in the main list
    const reorderedFields = Array.from(fields);
    const [removed] = reorderedFields.splice(source.index, 1);
    reorderedFields.splice(destination.index, 0, removed);
    setFields(reorderedFields);
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Form Builder</h1>

      {/* Main Fields Area */}
      <DragDropContext onDragEnd={onDragEnd}>
        <Droppable droppableId="droppable-fields">
          {(provided) => (
            <div
              ref={provided.innerRef}
              {...provided.droppableProps}
              style={{
                display: 'flex',
                flexDirection: 'column',
                gap: '10px',
                width: '300px',
                border: '1px solid black',
                padding: '20px',
              }}
            >
              {fields.map((field, index) => (
                <Draggable key={field.id} draggableId={field.id} index={index}>
                  {(provided) => (
                    <div
                      ref={provided.innerRef}
                      {...provided.draggableProps}
                      {...provided.dragHandleProps}
                      style={{
                        ...provided.draggableProps.style,
                        padding: '10px',
                        backgroundColor: '#f0f0f0',
                        border: '1px solid gray',
                        borderRadius: '5px',
                      }}
                    >
                      {field.label}
                    </div>
                  )}
                </Draggable>
              ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>

        {/* Container Fields Area */}
        <Droppable droppableId="droppable-container">
          {(provided) => (
            <div
              ref={provided.innerRef}
              {...provided.droppableProps}
              style={{
                marginTop: '20px',
                border: '1px solid blue',
                padding: '20px',
                minHeight: '100px',
                display: 'flex',
                flexDirection: 'column',
                gap: '10px',
              }}
            >
              <h2>Container Field</h2>
              {containerFields.map((field, index) => (
                <Draggable key={field.id} draggableId={field.id} index={index}>
                  {(provided) => (
                    <div
                      ref={provided.innerRef}
                      {...provided.draggableProps}
                      {...provided.dragHandleProps}
                      style={{
                        ...provided.draggableProps.style,
                        padding: '10px',
                        backgroundColor: '#d3e7ff',
                        border: '1px solid gray',
                        borderRadius: '5px',
                      }}
                    >
                      {field.label}
                    </div>
                  )}
                </Draggable>
              ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </DragDropContext>
    </div>
  );
};

export default FormBuilder;
