// api.js

// Fetch a specific form by ID
export const fetchForm = async (id) => {
    const response = await fetch(`/forms/${id}`);
    return response.json();
  };

  // Update a form by ID
  export const updateForm = async (id, formData) => {
    const response = await fetch(`/forms/update/${id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(formData),
    });
    return response.json();
  };

  // Fetch all forms
  export const fetchAllForms = async () => {
    const response = await fetch('/forms/list');
    return response.json();
  };

  // Save a new form
  export const saveForm = async (formData) => {
    const response = await fetch('/forms/save', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(formData),
    });
    return response.json();
  };
