import React, { useState } from 'react';
import { DragDropContext, Droppable, Draggable } from '@hello-pangea/dnd';
import axios from 'axios';

// Initial component fields that can be dragged into the form
const initialComponentFields = [
  { id: 'field-1', label: 'Text Input', type: 'text' },
  { id: 'field-2', label: 'Email Input', type: 'email' },
  { id: 'field-3', label: 'Date Input', type: 'date' },
];

// Component to render a field input
const FieldInput = ({ label, type, required }) => (
  <div>
    <label>{label}</label>
    <input type={type} required={required} />
  </div>
);

const FormBuilder = () => {
  const [componentFields, setComponentFields] = useState(initialComponentFields);
  const [designFields, setDesignFields] = useState([]);
  const [selectedField, setSelectedField] = useState(null);

  // Handles the dragging and dropping of components into the design area
  const onDragEnd = (result) => {
    const { destination, source } = result;
    if (!destination) return; // No movement

    const updatedDesignFields = Array.from(designFields);
    const [removed] = updatedDesignFields.splice(source.index, 1);
    updatedDesignFields.splice(destination.index, 0, removed);

    setDesignFields(updatedDesignFields);
  };

  // Handle click on a field in the design area to edit it
  const handleFieldClick = (field) => {
    setSelectedField(field);
  };

  // Handle field properties change (such as changing the label)
  const handleFieldChange = (e) => {
    if (selectedField) {
      const updatedField = { ...selectedField, label: e.target.value };
      setSelectedField(updatedField);

      const updatedDesignFields = designFields.map((field) =>
        field.id === selectedField.id ? updatedField : field
      );
      setDesignFields(updatedDesignFields);
    }
  };

  // Save the form with the current components in the design area
  const saveForm = async () => {
    const formData = { title: 'Custom Form', fields: designFields };
    try {
      const response = await axios.post('/api/save-form', formData);
      alert('Form saved successfully!');
    } catch (error) {
      console.error('Error saving form:', error);
    }
  };

  // Filter undefined or invalid components before rendering
  const validDesignFields = designFields.filter((field) => field && field.id);
  const validComponentFields = componentFields.filter((field) => field && field.id);

  return (
    <div style={{ display: 'flex', padding: '20px' }}>
      {/* Components Sidebar */}
      <div style={{ width: '200px', padding: '10px' }}>
        <h2>Components</h2>
        <DragDropContext onDragEnd={onDragEnd}>
          <Droppable droppableId="componentsSidebar">
            {(provided) => (
              <div ref={provided.innerRef} {...provided.droppableProps}>
                {validComponentFields.map((field, index) => (
                  <Draggable draggableId={field.id} index={index} key={field.id}>
                    {(provided) => (
                      <div
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        style={{
                          padding: '10px',
                          marginBottom: '10px',
                          border: '1px solid #ccc',
                          borderRadius: '5px',
                          background: '#f9f9f9',
                        }}
                      >
                        {field.label}
                      </div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>
        </DragDropContext>
      </div>

      {/* Form Builder Area */}
      <div style={{ flex: 1, padding: '10px', border: '1px solid #ccc' }}>
        <h2>Form Builder</h2>
        <DragDropContext onDragEnd={onDragEnd}>
          <Droppable droppableId="designArea">
            {(provided) => (
              <div ref={provided.innerRef} {...provided.droppableProps}>
                {validDesignFields.map((field, index) => (
                  <Draggable key={field.id} draggableId={field.id} index={index}>
                    {(provided) => (
                      <div
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        style={{
                          padding: '10px',
                          marginBottom: '10px',
                          border: '1px solid #ccc',
                          borderRadius: '5px',
                          background: '#f0f0f0',
                        }}
                        onClick={() => handleFieldClick(field)}
                      >
                        {field.label}
                      </div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>
        </DragDropContext>

        {/* Field Properties Editor */}
        {selectedField && (
          <div>
            <h3>Properties</h3>
            <input
              type="text"
              value={selectedField.label}
              onChange={handleFieldChange}
              placeholder="Field label"
            />
            <button onClick={saveForm}>Save Form</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default FormBuilder;
