import React, { useState, useEffect, useRef } from "react";
import { DragDropContext, Droppable } from "@hello-pangea/dnd";
import { fetchAllForms, saveForm } from './api';
import { FieldEditor } from './FieldEditor';
import { FloatingLabelField } from './FloatingLabelField';
import { FieldList } from './FieldList';

const FormBuilder = () => {
  const [fields, setFields] = useState(initialFields);
  const [containerFields, setContainerFields] = useState([]);
  const [selectedField, setSelectedField] = useState(null);
  const [formList, setFormList] = useState([]);
  const [formName, setFormName] = useState("");
  const [formId, setFormId] = useState(null);

  const inputRefs = useRef({});

  useEffect(() => {
    fetchAllForms().then((forms) => {
      setFormList(forms);
    });
  }, []);

  const handleSubmit = () => {
    const formData = containerFields.reduce((acc, field) => {
      const fieldElement = inputRefs.current[field.id];
      if (fieldElement) {
        acc[field.id] = fieldElement.value;
      }
      return acc;
    }, {});

    const payload = {
      form_name: formName,
      form_data: formData,
    };

    saveForm(formId, payload).then((response) => {
      console.log("Form submitted successfully", response);
    }).catch((error) => {
      console.error("Error submitting form", error);
    });
  };

  return (
    <div>
      <h2>Form Builder</h2>
      <input
        type="text"
        value={formName}
        onChange={(e) => setFormName(e.target.value)}
        placeholder="Enter Form Name"
      />
      <FieldList
        fields={fields}
        setContainerFields={setContainerFields}
        setSelectedField={setSelectedField}
      />
      {selectedField && <FieldEditor field={selectedField} />}
      <DragDropContext onDragEnd={onDragEnd}>
        {/* Drag and Drop logic here */}
      </DragDropContext>
      <button onClick={handleSubmit}>Submit</button>
    </div>
  );
};

export default FormBuilder;
