import React, { useState } from "react";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import axios from "axios";

const availableFields = [
    { id: "leadType", label: "Lead Type", value: "" },
    { id: "firstName", label: "First Name", value: "" },
    { id: "email", label: "Email", value: "" },
    { id: "dob", label: "Date of Birth", value: "" },
    { id: "address", label: "Address Line 1", value: "" },
];

const handleFieldChange = (index, newValue) => {
    setFormFields((prevFields) =>
        prevFields.map((field, i) =>
            i === index ? { ...field, value: newValue } : field
        )
    );
};




const FormBuilder = () => {
    const [formFields, setFormFields] = useState([]);

    const onDragEnd = (result) => {
        if (!result.destination) return;

        const newFields = Array.from(formFields);
        newFields.splice(result.destination.index, 0, availableFields[result.source.index]);
        setFormFields(newFields);
    };

    const saveForm = async () => {
        const formData = {
            form_name: "Custom Form",
            form_data: JSON.stringify(formFields),
        };
        await axios.post("http://localhost:8000/api/forms", formData);
        alert("Form saved!");
    };

    return (
        <div>
            <h1>Form Builder</h1>
            <DragDropContext onDragEnd={onDragEnd}>
                <div style={{ display: "flex", gap: "2rem" }}>
                    {/* Available Fields */}
                    <Droppable droppableId="availableFields">
                        {(provided) => (
                            <div ref={provided.innerRef} {...provided.droppableProps} style={{ border: "1px solid black", padding: "1rem", width: "200px" }}>
                                <h3>Available Fields</h3>
                                {availableFields.map((field, index) => (
                                    <Draggable key={field.id} draggableId={field.id} index={index}>
                                        {(provided) => (
                                            <div
                                                ref={provided.innerRef}
                                                {...provided.draggableProps}
                                                {...provided.dragHandleProps}
                                                style={{ marginBottom: "10px", padding: "5px", border: "1px solid gray" }}
                                            >
                                                {field.label}
                                            </div>
                                        )}
                                    </Draggable>
                                ))}
                                {provided.placeholder}
                            </div>
                        )}
                    </Droppable>

                    {/* Form Canvas */}
                    <Droppable droppableId="formFields">
                        {(provided) => (
                            <div ref={provided.innerRef} {...provided.droppableProps} style={{ border: "1px solid black", padding: "1rem", width: "300px" }}>
                                <h3>Form Canvas</h3>
                                {formFields.map((field, index) => (
                                    <div key={index} style={{ marginBottom: "10px", padding: "5px", border: "1px solid gray" }}>
                                        {field.label}
                                    </div>
                                ))}
                                {provided.placeholder}
                            </div>
                        )}
                    </Droppable>
                </div>
            </DragDropContext>
            <button onClick={saveForm} style={{ marginTop: "20px" }}>
                Save Form
            </button>
        </div>
    );
};

export default FormBuilder;
