import React, { useState, useEffect, useRef } from "react";
import { DragDropContext, Droppable, Draggable } from "@hello-pangea/dnd";
import axios from "axios";

const initialFields = [
  { id: "field-1", label: "Text Input", type: "text", placeholder: "Enter text", required: false, maxLength: "", minLength: "" },
  { id: "field-2", label: "Textarea", type: "textarea", placeholder: "Enter your message", required: false, maxLength: "", minLength: "" },
  { 
    id: "field-3", 
    label: "Select Dropdown", 
    type: "select", 
    options: ["Option 1", "Option 2", "Option 3"], 
    required: false 
  },
  { id: "field-4", label: "Checkbox", type: "checkbox", required: false },
  { id: "field-5", label: "Today", type: "radio", options: ["A", "B", "C", "D"], selectedOption: "A", required: false },
  { id: "field-6", label: "Date Picker", type: "date", required: false },
  { id: "field-7", label: "File Upload", type: "file", required: false },
];

const FormBuilder = () => {
  const [fields, setFields] = useState(initialFields);
  const [containerFields, setContainerFields] = useState([]);
  const [selectedField, setSelectedField] = useState(null);

  const inputRefs = useRef({});

  const API_URL = 'http://your-laravel-app.test/api/forms';

  const fetchForm = async (id) => {
    try {
      const response = await axios.get(`${API_URL}/${id}`);
      console.log(response.data);
    } catch (error) {
      console.error("Error fetching form:", error);
    }
  };

  const updateForm = async (id, formData) => {
    try {
      const response = await axios.put(`${API_URL}/update/${id}`, formData);
      console.log("Form updated:", response.data);
    } catch (error) {
      console.error("Error updating form:", error);
    }
  };

  const getAllForms = async () => {
    try {
      const response = await axios.get(`${API_URL}/list`);
      console.log(response.data);
    } catch (error) {
      console.error("Error getting forms:", error);
    }
  };

  const saveForm = async (formData) => {
    try {
      const response = await axios.post(`${API_URL}/save`, formData);
      console.log("Form saved:", response.data);
    } catch (error) {
      console.error("Error saving form:", error);
      if (error.response) {
        console.error("Response Data:", error.response.data);
        console.error("Response Status:", error.response.status);
      } else {
        console.error("Error Message:", error.message);
      }
    }
  };

  // Focus logic
  useEffect(() => {
    if (selectedField) {
      const inputElement = inputRefs.current[selectedField.id];
      if (inputElement) {
        if (document.activeElement !== inputElement) {
          inputElement.focus();
        }
      }
    }
  }, [selectedField]);

  const onDragEnd = (result) => {
    const { source, destination } = result;
    if (!destination) return;

    if (source.droppableId === "fields" && destination.droppableId === "container") {
      const draggedField = fields[source.index];
      const newField = {
        ...draggedField,
        id: `${draggedField.id}-${Date.now()}`,
      };
      setContainerFields((prev) => [...prev, newField]);
    } else if (source.droppableId === "container" && destination.droppableId === "container") {
      const reorderedFields = Array.from(containerFields);
      const [moved] = reorderedFields.splice(source.index, 1);
      reorderedFields.splice(destination.index, 0, moved);
      setContainerFields(reorderedFields);
    } else if (source.droppableId === "container" && destination.droppableId === "fields") {
      const updatedContainerFields = Array.from(containerFields);
      updatedContainerFields.splice(source.index, 1);
      setContainerFields(updatedContainerFields);

      const removedField = containerFields[source.index];
      if (selectedField && selectedField.id === removedField.id) {
        setSelectedField(null);
      }
    }
  };

  const handleFieldUpdate = (id, key, value) => {
    const updateFields = (fieldsList) =>
      fieldsList.map((field) =>
        field.id === id ? { ...field, [key]: key === "required" ? !field.required : value } : field
      );

    setFields(updateFields(fields));
    setContainerFields(updateFields(containerFields));
    setSelectedField((prev) => (prev?.id === id ? { ...prev, [key]: value } : prev));
  };

  const FieldEditor = ({ field }) => {
    const [localField, setLocalField] = useState(field);

    const handleLocalChange = (key, value) => {
      setLocalField((prev) => ({ ...prev, [key]: value }));
      handleFieldUpdate(field.id, key, value);
    };

    const addOption = () => {
      setLocalField((prev) => {
        const newOptions = [...prev.options, `Option ${prev.options.length + 1}`];
        handleFieldUpdate(field.id, 'options', newOptions);
        return { ...prev, options: newOptions };
      });
    };

    const removeOption = (index) => {
      setLocalField((prev) => {
        const newOptions = prev.options.filter((_, idx) => idx !== index);
        handleFieldUpdate(field.id, 'options', newOptions);
        return { ...prev, options: newOptions };
      });
    };

    return (
      <div style={{ border: "1px solid #ddd", padding: "10px", marginTop: "10px" }}>
        <h4>Edit Field</h4>
        <label>
          Label:
          <input
            type="text"
            value={localField.label}
            onChange={(e) => handleLocalChange("label", e.target.value)}
          />
        </label>
        <br />
        {localField.type === "radio" && (
          <>
            <label>{localField.label}:</label>
            <div style={{ display: "flex", gap: "15px", marginTop: "10px" }}>
              {localField.options.map((option, index) => (
                <label key={index} style={{ display: "flex", alignItems: "center" }}>
                  <input
                    type="radio"
                    name={localField.id} // Ensures the radio buttons belong to the same group
                    value={option}
                    checked={localField.selectedOption === option} // Check the selected option
                    onChange={() => handleLocalChange("selectedOption", option)} // Update selected option
                  />
                  {option}
                </label>
              ))}
            </div>
            <button type="button" onClick={addOption} style={{ marginTop: "10px" }}>
              Add Option
            </button>
            <br />
          </>
        )}
        <label>
          Required:
          <input
            type="checkbox"
            checked={localField.required}
            onChange={() => handleLocalChange("required", !localField.required)}
          />
        </label>
      </div>
    );
  };

  const FloatingLabelField = React.forwardRef(({ field, onClick }, ref) => {
    return (
      <div style={{ position: "relative", marginBottom: "20px" }}>
        <input
          ref={ref}
          id={field.id}
          type={field.type}
          placeholder={field.placeholder}
          required={field.required}
          maxLength={field.maxLength}
          minLength={field.minLength}
          onClick={onClick}
          className="input-field"
          style={{
            padding: "10px",
            width: "100%",
            minWidth: "200px",
            transition: "all 0.2s",
            border: "1px solid #ccc",
            borderRadius: "4px",
            fontSize: "16px",
          }}
        />
        <label
          htmlFor={field.id}
          style={{
            position: "absolute",
            top: "10px",
            left: "10px",
            fontSize: "16px",
            transition: "all 0.2s",
            transform: "translateY(-20px)",
            pointerEvents: "none",
            opacity: field.value || field.placeholder ? "0.7" : "1",
          }}
        >
          {field.label}
        </label>
      </div>
    );
  });

  return (
    <div>
      <h2>Form Builder</h2>
      <DragDropContext onDragEnd={onDragEnd}>
        <Droppable droppableId="fields">
          {(provided) => (
            <div
              ref={provided.innerRef}
              style={{ padding: "10px", backgroundColor: "#f4f4f4", borderRadius: "5px" }}
              {...provided.droppableProps}
            >
              {fields.map((field, index) => (
                <Draggable key={field.id} draggableId={field.id} index={index}>
                  {(provided) => (
                    <div
                      ref={provided.innerRef}
                      {...provided.draggableProps}
                      {...provided.dragHandleProps}
                      style={{
                        ...provided.draggableProps.style,
                        border: "1px solid #ddd",
                        padding: "10px",
                        marginBottom: "10px",
                        backgroundColor: "#fff",
                        borderRadius: "5px",
                      }}
                    >
                      {field.label}
                    </div>
                  )}
                </Draggable>
              ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>

        <Droppable droppableId="container">
          {(provided) => (
            <div
              ref={provided.innerRef}
              style={{
                padding: "10px",
                marginLeft: "20px",
                backgroundColor: "#e9e9e9",
                borderRadius: "5px",
                minHeight: "200px",
                width: "300px",
              }}
              {...provided.droppableProps}
            >
              <h3>Preview</h3>
              {containerFields.map((field, index) => (
                <Draggable key={field.id} draggableId={field.id} index={index}>
                  {(provided) => (
                    <div
                      ref={provided.innerRef}
                      {...provided.draggableProps}
                      {...provided.dragHandleProps}
                      style={{
                        ...provided.draggableProps.style,
                        border: "1px solid #ddd",
                        padding: "10px",
                        marginBottom: "10px",
                        backgroundColor: "#fff",
                        borderRadius: "5px",
                      }}
                    >
                      <FloatingLabelField
                        ref={inputRefs}
                        field={field}
                        onClick={() => setSelectedField(field)}
                      />
                    </div>
                  )}
                </Draggable>
              ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </DragDropContext>
      {selectedField && <FieldEditor field={selectedField} />}
    </div>
  );
};

export default FormBuilder;
