import React, { useState } from "react";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";

const availableFields = [
    { id: "leadType", label: "Lead Type", value: "" },
    { id: "firstName", label: "First Name", value: "" },
    { id: "email", label: "Email", value: "" },
    { id: "dob", label: "Date of Birth", value: "" },
    { id: "address", label: "Address Line 1", value: "" },
];

const FormBuilder = () => {
    const [formFields, setFormFields] = useState([]);

    const onDragEnd = (result) => {
        const { source, destination } = result;

        if (!destination) return;

        if (source.droppableId === "availableFields" && destination.droppableId === "formFields") {
            const draggedField = availableFields[source.index];
            if (!formFields.some((field) => field.id === draggedField.id)) {
                setFormFields((prevFields) => [...prevFields, draggedField]);
            }
        }

        if (source.droppableId === "formFields" && destination.droppableId === "formFields") {
            const reorderedFields = Array.from(formFields);
            const [movedField] = reorderedFields.splice(source.index, 1);
            reorderedFields.splice(destination.index, 0, movedField);
            setFormFields(reorderedFields);
        }
    };

    return (
        <div>
            <h1>Form Builder</h1>
            <DragDropContext onDragEnd={onDragEnd}>
                <div style={{ display: "flex", gap: "2rem" }}>
                    <Droppable droppableId="availableFields">
                        {(provided) => (
                            <div
                                ref={provided.innerRef}
                                {...provided.droppableProps}
                                style={{
                                    border: "1px solid black",
                                    padding: "1rem",
                                    width: "200px",
                                    background: "#f7f7f7",
                                }}
                            >
                                <h3>Available Fields</h3>
                                {availableFields.map((field, index) => (
                                    <Draggable key={field.id} draggableId={field.id} index={index}>
                                        {(provided) => (
                                            <div
                                                ref={provided.innerRef}
                                                {...provided.draggableProps}
                                                {...provided.dragHandleProps}
                                                style={{
                                                    ...provided.draggableProps.style,
                                                    userSelect: "none",
                                                    padding: 10,
                                                    margin: "0 0 8px 0",
                                                    background: "lightblue",
                                                    borderRadius: "4px",
                                                }}
                                            >
                                                {field.label}
                                            </div>
                                        )}
                                    </Draggable>
                                ))}
                                {provided.placeholder}
                            </div>
                        )}
                    </Droppable>

                    <Droppable droppableId="formFields">
                        {(provided) => (
                            <div
                                ref={provided.innerRef}
                                {...provided.droppableProps}
                                style={{
                                    border: "1px solid black",
                                    padding: "1rem",
                                    width: "300px",
                                    background: "#e9ecef",
                                }}
                            >
                                <h3>Form Canvas</h3>
                                {formFields.map((field, index) => (
                                    <Draggable key={field.id} draggableId={field.id} index={index}>
                                        {(provided) => (
                                            <div
                                                ref={provided.innerRef}
                                                {...provided.draggableProps}
                                                {...provided.dragHandleProps}
                                                style={{
                                                    ...provided.draggableProps.style,
                                                    userSelect: "none",
                                                    padding: 10,
                                                    margin: "0 0 8px 0",
                                                    background: "lightgreen",
                                                    borderRadius: "4px",
                                                }}
                                            >
                                                {field.label}
                                            </div>
                                        )}
                                    </Draggable>
                                ))}
                                {provided.placeholder}
                            </div>
                        )}
                    </Droppable>
                </div>
            </DragDropContext>
        </div>
    );
};

export default FormBuilder;
