import React, { useState } from "react";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import axios from "axios";

const availableFields = [
    { id: "leadType", label: "Lead Type" },
    { id: "firstName", label: "First Name" },
    { id: "email", label: "Email" },
    { id: "dob", label: "Date of Birth" },
    { id: "address", label: "Address Line 1" },
];

const FormBuilder = () => {
    const [formFields, setFormFields] = useState([]);

    const onDragEnd = (result) => {
        if (!result.destination) return;

        // Handle drag-and-drop logic
        const newFields = Array.from(formFields);
        if (result.source.droppableId === "availableFields") {
            const draggedField = availableFields[result.source.index];
            newFields.splice(result.destination.index, 0, draggedField);
        } else {
            const [movedField] = newFields.splice(result.source.index, 1);
            newFields.splice(result.destination.index, 0, movedField);
        }

        setFormFields(newFields);
    };

    const saveForm = async () => {
        const formData = {
            form_name: "Custom Form",
            form_data: JSON.stringify(formFields),
        };
        await axios.post("http://localhost:8000/api/forms", formData);
        alert("Form saved!");
    };

    return (
        <div>
            <h1>Form Builder</h1>
            <DragDropContext onDragEnd={onDragEnd}>
                <div style={{ display: "flex", gap: "2rem" }}>
                    {/* Available Fields */}
                    <Droppable droppableId="availableFields" isDropDisabled={true}>
                        {(provided) => (
                            <div
                                ref={provided.innerRef}
                                {...provided.droppableProps}
                                style={{ border: "1px solid black", padding: "1rem", width: "200px" }}
                            >
                                <h3>Available Fields</h3>
                                {availableFields.map((field, index) => (
                                    <Draggable key={field.id} draggableId={field.id} index={index}>
                                        {(provided) => (
                                            <div
                                                ref={provided.innerRef}
                                                {...provided.draggableProps}
                                                {...provided.dragHandleProps}
                                                style={{
                                                    userSelect: "none",
                                                    padding: "10px",
                                                    marginBottom: "10px",
                                                    background: "lightblue",
                                                    border: "1px solid gray",
                                                    borderRadius: "4px",
                                                }}
                                            >
                                                {field.label}
                                            </div>
                                        )}
                                    </Draggable>
                                ))}
                                {provided.placeholder}
                            </div>
                        )}
                    </Droppable>

                    {/* Form Canvas */}
                    <Droppable droppableId="formFields">
                        {(provided) => (
                            <div
                                ref={provided.innerRef}
                                {...provided.droppableProps}
                                style={{ border: "1px solid black", padding: "1rem", width: "300px" }}
                            >
                                <h3>Form Canvas</h3>
                                {formFields.map((field, index) => (
                                    <Draggable key={field.id} draggableId={`form-${field.id}`} index={index}>
                                        {(provided) => (
                                            <div
                                                ref={provided.innerRef}
                                                {...provided.draggableProps}
                                                {...provided.dragHandleProps}
                                                style={{
                                                    userSelect: "none",
                                                    padding: "10px",
                                                    marginBottom: "10px",
                                                    background: "lightgreen",
                                                    border: "1px solid gray",
                                                    borderRadius: "4px",
                                                }}
                                            >
                                                {field.label}
                                            </div>
                                        )}
                                    </Draggable>
                                ))}
                                {provided.placeholder}
                            </div>
                        )}
                    </Droppable>
                </div>
            </DragDropContext>
            <button onClick={saveForm} style={{ marginTop: "20px" }}>
                Save Form
            </button>
        </div>
    );
};

export default FormBuilder;
