// src/formApi.js
import axios from 'axios';

// Replace with your Laravel API URL
const API_URL = 'http://your-laravel-api-url';

// Submit a new form
export const submitForm = async (formData) => {
  try {
    const response = await axios.post(`${API_URL}/forms/save`, formData);
    return response.data;  // Return the saved form data
  } catch (error) {
    console.error('Error saving form:', error);
    throw error;  // Propagate the error for further handling in the calling component
  }
};

// Fetch a form by ID
export const fetchForm = async (id) => {
  try {
    const response = await axios.get(`${API_URL}/forms/${id}`);
    return response.data;  // Return the fetched form data
  } catch (error) {
    console.error('Error fetching form:', error);
    throw error;  // Propagate the error for further handling in the calling component
  }
};

// Fetch all forms
export const fetchAllForms = async () => {
  try {
    const response = await axios.get(`${API_URL}/forms/list`);
    return response.data;  // Return the list of forms
  } catch (error) {
    console.error('Error fetching forms:', error);
    throw error;
  }
};
