import React, { useState, useEffect, useRef } from "react";
import { DragDropContext, Droppable, Draggable } from "@hello-pangea/dnd";
import { fetchForm, updateForm, fetchAllForms, saveForm } from './api';

const initialFields = [
  // Initial fields code...
];

const FormBuilder = () => {
  const [fields, setFields] = useState(initialFields);
  const [containerFields, setContainerFields] = useState([]);
  const [selectedField, setSelectedField] = useState(null);
  const [formList, setFormList] = useState([]);
  const [formId, setFormId] = useState(null);

  const inputRefs = useRef({});

  // Fetch all forms on component mount
  useEffect(() => {
    fetchAllForms().then((forms) => {
      setFormList(forms);
    });
  }, []);

  // Focus logic
  useEffect(() => {
    if (selectedField) {
      const inputElement = inputRefs.current[selectedField.id];
      if (inputElement) {
        // Ensuring that we focus the input only if it's not already focused
        if (document.activeElement !== inputElement) {
          inputElement.focus();
        }
      }
    }
  }, [selectedField]);

  // Handle drag end for fields
  const onDragEnd = (result) => {
    // Drag logic code...
  };

  // Update field in both fields and containerFields
  const handleFieldUpdate = (id, key, value) => {
    // Field update logic code...
  };

  // Field editor component for updating a field
  const FieldEditor = ({ field }) => {
    // Field editor code...
  };

  // Floating Label Field Component
  const FloatingLabelField = React.forwardRef(({ field, onClick }, ref) => {
    return (
      <div style={{ position: "relative", marginBottom: "20px" }}>
        <input
          ref={ref}
          id={field.id}
          type={field.type}
          placeholder={field.placeholder}
          required={field.required}
          maxLength={field.maxLength}
          minLength={field.minLength}
          onClick={onClick}
          className="input-field"
          style={{
            padding: "10px",
            width: "100%",
            minWidth: "200px",
            transition: "all 0.2s",
            border: "1px solid #ccc",
            borderRadius: "4px",
            fontSize: "16px",
          }}
        />
        <label
          htmlFor={field.id}
          style={{
            position: "absolute",
            top: "10px",
            left: "10px",
            fontSize: "16px",
            transition: "all 0.2s",
            transform: "translateY(-20px)",
            pointerEvents: "none",
            opacity: field.value || field.placeholder ? "0.7" : "1",
          }}
        >
          {field.label}
        </label>
      </div>
    );
  });

  // Handle form submission
  const handleSubmit = () => {
    const formData = containerFields.reduce((acc, field) => {
      const fieldElement = inputRefs.current[field.id];
      if (fieldElement) {
        acc[field.id] = fieldElement.value;
      }
      return acc;
    }, {});

    saveForm(formId, formData).then((response) => {
      console.log("Form submitted successfully", response);
    }).catch((error) => {
      console.error("Error submitting form", error);
    });
  };

  // Handle fetch form
  const handleFetchForm = () => {
    if (formId) {
      fetchForm(formId).then((formData) => {
        console.log("Form data fetched:", formData);
      }).catch((error) => {
        console.error("Error fetching form data", error);
      });
    }
  };

  // Handle update form
  const handleUpdateForm = () => {
    if (formId) {
      updateForm(formId, containerFields).then((response) => {
        console.log("Form updated successfully", response);
      }).catch((error) => {
        console.error("Error updating form", error);
      });
    }
  };

  return (
    <div style={{ display: "flex", flexDirection: "column", alignItems: "center", justifyContent: "center" }}>
      <h2 style={{ textAlign: "center" }}>Form Builder</h2>

      {/* Fetch and display all forms */}
      <div>
        <h3>Select Form to Edit</h3>
        <select onChange={(e) => setFormId(e.target.value)} value={formId}>
          <option value="">-- Select a form --</option>
          {formList.map((form) => (
            <option key={form.id} value={form.id}>
              {form.name}
            </option>
          ))}
        </select>
      </div>

      <DragDropContext onDragEnd={onDragEnd}>
        <div style={{ display: "flex", justifyContent: "space-between", width: "80%", margin: "20px 0" }}>
          <Droppable droppableId="fields">
            {(provided) => (
              <div
                ref={provided.innerRef}
                style={{ padding: "10px", backgroundColor: "#f4f4f4", borderRadius: "5px", width: "45%" }}
              >
                {fields.map((field, index) => (
                  <Draggable key={field.id} draggableId={field.id} index={index}>
                    {(provided) => (
                      <div
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        style={{
                          ...provided.draggableProps.style,
                          marginBottom: "10px",
                          padding: "10px",
                          backgroundColor: "#fff",
                          border: "1px solid #ddd",
                          borderRadius: "5px",
                        }}
                      >
                        <div>{field.label}</div>
                      </div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>

          {/* Fields inside the form */}
          <Droppable droppableId="container">
            {(provided) => (
              <div
                ref={provided.innerRef}
                style={{ padding: "10px", backgroundColor: "#f4f4f4", borderRadius: "5px", width: "45%" }}
              >
                {containerFields.map((field, index) => (
                  <Draggable key={field.id} draggableId={field.id} index={index}>
                    {(provided) => (
                      <div
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        style={{
                          ...provided.draggableProps.style,
                          marginBottom: "10px",
                          padding: "10px",
                          backgroundColor: "#fff",
                          border: "1px solid #ddd",
                          borderRadius: "5px",
                        }}
                      >
                        <FloatingLabelField
                          ref={(el) => (inputRefs.current[field.id] = el)}
                          field={field}
                          onClick={() => setSelectedField(field)}
                        />
                      </div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>
        </div>
      </DragDropContext>

      {selectedField && <FieldEditor field={selectedField} />}

      {/* Button section for Fetch, Update, and Submit */}
      <div style={{ marginTop: "20px", display: "flex", justifyContent: "space-around", width: "100%" }}>
        <button onClick={handleFetchForm} style={{ padding: "10px 20px" }}>
          Fetch Form
        </button>
        <button onClick={handleUpdateForm} style={{ padding: "10px 20px" }}>
          Update Form
        </button>
        <button onClick={handleSubmit} style={{ padding: "10px 20px" }}>
          Submit Form
        </button>
      </div>
    </div>
  );
};

export default FormBuilder;
