import React, { useEffect, useRef } from "react";

export  const FloatingLabelField = React.forwardRef(({ field, onClick }, ref) => {
  const inputRef = useRef(null);

  useEffect(() => {
    inputRefs.current[field.id] = inputRef.current;
    console.log("Field reference updated:", field.id);
  }, [field.id]);

  const renderInput = () => {
    switch (field.type) {
      case "text":
      case "textarea":
        return (
          <input
            ref={inputRef}
            id={field.id}
            type={field.type}
            placeholder={field.placeholder}
            required={field.required}
            maxLength={field.maxLength}
            minLength={field.minLength}
            onClick={onClick}
            className="input-field"
            style={{
              padding: "10px",
              width: "100%",
              minWidth: "200px",
              transition: "all 0.2s",
              border: "1px solid #ccc",
              borderRadius: "4px",
              fontSize: "16px",
            }}
          />
        );

      case "select":
        return (
          <select
            ref={inputRef}
            id={field.id}
            required={field.required}
            onClick={onClick}
            style={{
              padding: "10px",
              width: "100%",
              minWidth: "200px",
              transition: "all 0.2s",
              border: "1px solid #ccc",
              borderRadius: "4px",
              fontSize: "16px",
            }}
          >
            {field.options?.map((option, index) => (
              <option key={index} value={option}>
                {option}
              </option>
            ))}
          </select>
        );

      case "checkbox":
        return (
          <input
            ref={inputRef}
            id={field.id}
            type="checkbox"
            required={field.required}
            onClick={onClick}
            style={{
              width: "20px",
              height: "20px",
              border: "1px solid #ccc",
            }}
          />
        );

      case "radio":
        return (
          <div>
            {field.options?.map((option, index) => (
              <label key={index}>
                <input
                  ref={inputRef}
                  type="radio"
                  name={field.id}
                  value={option}
                  required={field.required}
                  onClick={onClick}
                  style={{
                    width: "20px",
                    height: "20px",
                    marginRight: "10px",
                  }}
                />
                {option}
              </label>
            ))}
          </div>
        );

      case "date":
        return (
          <input
            ref={inputRef}
            id={field.id}
            type="date"
            required={field.required}
            onClick={onClick}
            style={{
              padding: "10px",
              width: "100%",
              minWidth: "200px",
              transition: "all 0.2s",
              border: "1px solid #ccc",
              borderRadius: "4px",
              fontSize: "16px",
            }}
          />
        );

      case "file":
        return (
          <input
            ref={inputRef}
            id={field.id}
            type="file"
            required={field.required}
            onClick={onClick}
            style={{
              width: "100%",
              padding: "10px",
              border: "1px solid #ccc",
              borderRadius: "4px",
            }}
          />
        );

      default:
        return null;
    }
  };

  return (
    <div style={{ position: "relative", marginBottom: "20px" }}>
      {renderInput()}
      <label
        htmlFor={field.id}
        style={{
          position: "absolute",
          top: "10px",
          left: "10px",
          fontSize: "16px",
          transition: "all 0.2s",
          transform: "translateY(-20px)",
          pointerEvents: "none",
          opacity: field.value || field.placeholder ? "0.7" : "1",
        }}
      >
        {field.label}
      </label>
    </div>
  );
});
