import React, { useState } from "react";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";

const availableFields = [
    { id: "leadType", label: "Lead Type" },
    { id: "firstName", label: "First Name" },
    { id: "email", label: "Email" },
    { id: "dob", label: "Date of Birth" },
    { id: "address", label: "Address Line 1" },
];

const FormBuilder = () => {
    const [formFields, setFormFields] = useState([]);

    const onDragEnd = (result) => {
        if (!result.destination) return;

        const { source, destination } = result;

        if (source.droppableId === "availableFields" && destination.droppableId === "formFields") {
            const draggedField = availableFields[source.index];
            setFormFields((prev) => [...prev.slice(0, destination.index), draggedField, ...prev.slice(destination.index)]);
        } else if (source.droppableId === "formFields" && destination.droppableId === "formFields") {
            const reorderedFields = Array.from(formFields);
            const [movedField] = reorderedFields.splice(source.index, 1);
            reorderedFields.splice(destination.index, 0, movedField);
            setFormFields(reorderedFields);
        }
    };

    return (
        <div>
            <h1>Form Builder</h1>
            <DragDropContext onDragEnd={onDragEnd}>
                <div style={{ display: "flex", gap: "20px" }}>
                    {/* Available Fields */}
                    <Droppable droppableId="availableFields">
                        {(provided) => (
                            <div
                                ref={provided.innerRef}
                                {...provided.droppableProps}
                                style={{ padding: "1rem", border: "1px solid black", width: "200px" }}
                            >
                                <h3>Available Fields</h3>
                                {availableFields.map((field, index) => (
                                    <Draggable key={field.id} draggableId={field.id} index={index}>
                                        {(provided) => (
                                            <div
                                                ref={provided.innerRef}
                                                {...provided.draggableProps}
                                                {...provided.dragHandleProps}
                                                style={{
                                                    padding: "10px",
                                                    marginBottom: "10px",
                                                    border: "1px solid gray",
                                                    borderRadius: "4px",
                                                    background: "#f0f0f0",
                                                    userSelect: "none",
                                                    ...provided.draggableProps.style,
                                                }}
                                            >
                                                {field.label}
                                            </div>
                                        )}
                                    </Draggable>
                                ))}
                                {provided.placeholder}
                            </div>
                        )}
                    </Droppable>

                    {/* Form Canvas */}
                    <Droppable droppableId="formFields">
                        {(provided) => (
                            <div
                                ref={provided.innerRef}
                                {...provided.droppableProps}
                                style={{ padding: "1rem", border: "1px solid black", width: "300px" }}
                            >
                                <h3>Form Canvas</h3>
                                {formFields.map((field, index) => (
                                    <Draggable key={field.id} draggableId={field.id} index={index}>
                                        {(provided) => (
                                            <div
                                                ref={provided.innerRef}
                                                {...provided.draggableProps}
                                                {...provided.dragHandleProps}
                                                style={{
                                                    padding: "10px",
                                                    marginBottom: "10px",
                                                    border: "1px solid gray",
                                                    borderRadius: "4px",
                                                    background: "#d4edda",
                                                    userSelect: "none",
                                                    ...provided.draggableProps.style,
                                                }}
                                            >
                                                {field.label}
                                            </div>
                                        )}
                                    </Draggable>
                                ))}
                                {provided.placeholder}
                            </div>
                        )}
                    </Droppable>
                </div>
            </DragDropContext>
        </div>
    );
};

export default FormBuilder;
