import React, { useEffect, useRef } from "react";

export const FloatingLabelField = React.forwardRef(({ field, onClick }, ref) => {
  const inputRef = useRef(null);

  useEffect(() => {
    // Store the input ref
  }, [field.id]);

  const renderInput = () => {
    switch (field.type) {
      case "text":
      case "textarea":
        return (
          <input
            ref={inputRef}
            id={field.id}
            type={field.type}
            placeholder={field.placeholder}
            required={field.required}
            maxLength={field.maxLength}
            minLength={field.minLength}
            onClick={onClick}
          />
        );
      // Other input types...
    }
  };

  return (
    <div>
      {renderInput()}
      <label htmlFor={field.id}>{field.label}</label>
    </div>
  );
});
